    private void BotonAcpActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonAcpActionPerformed
        String usuario = "Marcelo";
        String password = "marcelo123";
        String inv = this.txtUsuario.getText();
        String pass = this.txtPass.getText();
        if (usuario.equals(inv)&&password.equals(pass)){
            Dashboard panel = new Dashboard ();
            this.setVisible(false);            
            panel.setVisible(true);
        }else{
            JOptionPane.showMessageDialog(this, "Nombre de ususario/clave INCORRECTA", "ACCESO", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_BotonAcpActionPerformed

